import React, { useEffect, useState } from 'react';
import ContainerDefault from '~/components/layouts/ContainerDefault';
import Pagination from '~/components/elements/basic/Pagination';
import TableCustomerItems from '~/components/shared/tables/TableCustomerItems';
import FormSearchSimple from '~/components/shared/forms/FormSearchSimple';
import HeaderDashboard from '~/components/shared/headers/HeaderDashboard';
import { connect, useDispatch } from 'react-redux';
import { toggleDrawerMenu } from '~/store/app/action';
import VendorRepository from '~/repositories/VendorRepository';
import withAuth from '~/helpers/withAuth';
import Roles from '~/helpers/role';

const CustomersPage = () => {
    const dispatch = useDispatch();
    const [customers, setCustomers] = useState([]);
    const [pagination,setPagination] = useState({
        page:0,
        size:5,
        totalPages:0,
        totalElements:0,
        number:0
    })
    const initializeCustomers = async () =>{
        const vends = await VendorRepository.findAll(pagination.page,pagination.size);
        setCustomers([...vends.result.content]);
        setPagination({...pagination,...vends.result});
    }

    const next = async () =>{
        if(pagination.number < pagination.totalPages - 1){
            const vends = await VendorRepository.findAll(pagination.number + 1,pagination.size);
            setCustomers([...vends.result.content]);
            setPagination({...pagination,...vends.result});
        }
    }

    const prev = async () =>{
        if(pagination.number > 0){
            const vends = await VendorRepository.findAll(pagination.number - 1,pagination.size);
            setCustomers([...vends.result.content]);
            setPagination({...pagination,...vends.result});
        }
    }

    const onPageNumberClick = async (pageNumber) =>{
        const vends = await VendorRepository.findAll(pageNumber,pagination.size);
        setCustomers([...vends.result.content]);
        setPagination({...pagination,...vends.result});
    }

    useEffect(() => {
        dispatch(toggleDrawerMenu(false));
        initializeCustomers();
    }, []);
    return (
        <ContainerDefault title="Customers">
            <HeaderDashboard
                title="Customers"
                description="Customer Management"
            />
            <section className="ps-items-listing">
                <div className="ps-section__header simple">
                    <div className="ps-section__filter">
                        <FormSearchSimple />
                    </div>
                    <div className="ps-section__actions">
                        <a className="ps-btn success" href="#">
                            <i className="icon icon-plus mr-2"></i>Add Customer
                        </a>
                    </div>
                </div>
                <div className="ps-section__content">
                    <TableCustomerItems customers={customers} />
                </div>
                <div className="ps-section__footer">
                    <p>Show {pagination.numberOfElements} in {pagination.totalElements} items.</p>
                    <Pagination {...pagination} next={next} prev={prev} onPageNumberClick={onPageNumberClick}/>
                </div>
            </section>
        </ContainerDefault>
    );
};
export default connect((state) => state.app)(withAuth(CustomersPage)([Roles.admin]));
